window.addEventListener("DOMContentLoaded",()=>{const e=new IntersectionObserver(e=>{for(entry of e){const e=entry.target.getAttribute("id");entry.isIntersecting?document.querySelector(`nav a[href="#${e}"]`).classList.add("active"):document.querySelector(`nav a[href="#${e}"]`).classList.remove("active")}});[document.getElementById("jumbo"),document.getElementById("intro"),document.getElementById("roster"),document.getElementById("about"),document.getElementById("contact")].forEach(t=>{e.observe(t)})});